import React, { useState } from 'react';
import { Editor, EditorState, RichUtils } from 'draft-js';
import { Button, Paper } from '@mui/material';

const CustomEditor = () => {
  const [editorState, setEditorState] = useState(() => EditorState.createEmpty());

  const handleBoldClick = () => {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'BOLD'));
  };

  const handleItalicClick = () => {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'ITALIC'));
  };

  const handleUnderlineClick = () => {
    setEditorState(RichUtils.toggleInlineStyle(editorState, 'UNDERLINE'));
  };

  const handleChange = (newEditorState) => {
    setEditorState(newEditorState);
  };

  return (
    <Paper elevation={3} style={{ padding: '16px' }}>
      <div>
        <Button onClick={handleBoldClick} variant={editorState.getCurrentInlineStyle().has('BOLD') ? 'contained' : 'outlined'}>
          Bold
        </Button>
        <Button onClick={handleItalicClick} variant={editorState.getCurrentInlineStyle().has('ITALIC') ? 'contained' : 'outlined'}>
          Italic
        </Button>
        <Button onClick={handleUnderlineClick} variant={editorState.getCurrentInlineStyle().has('UNDERLINE') ? 'contained' : 'outlined'}>
          Underline
        </Button>
      </div>
      <Editor
        editorState={editorState}
        onChange={handleChange}
      />
    </Paper>
  );
};

export default CustomEditor;
