import { Button, Grid, IconButton, useTheme } from "@mui/material";
import "./styles.css";
import { MoreVert } from "@mui/icons-material";
import { useState } from "react";

function ButtonsGrid(){

    const theme = useTheme();

    const [followCheck, setFollowCheck] = useState(false);
    const [followState, setFollowState] = useState(false);
    async function followUnfollowCheck(){
        //meccanismo da backend per il check del follow unfollow
        //esempio simulativo: mettiamo che la funzione ponga che i due users si seguono
        setFollowCheck(true);
    }

    const onClickFollowButton = () => {
        //meccanismo da backend per l'aggiunta o rimozione del follower
        if(followCheck){
            //se si seguono deve essere rimosso il follow
            //operazioni da backend
            setFollowCheck(false);
        }else{
            //se non si seguono si deve aggiungere il follow
            //operazioni da backend
            setFollowCheck(true);
        }
    }

    return(
        <Grid className="profile-buttons-grid" container columnSpacing={1}>
            <Grid item>
                <Button className="buttonsgrid-button" variant="outlined">Follow</Button>
            </Grid>
            <Grid item>
                <Button variant="outlined">Subscribe</Button>
            </Grid>
            <Grid item>
                <Button variant="outlined">Message</Button>
            </Grid>
            <Grid item>
                <Button variant="outlined">Tip</Button>
            </Grid>
            <Grid item>
                <IconButton color={theme.palette.primary.light}>
                    <MoreVert style={{color: theme.palette.primary.dark}}/>
                </IconButton>
            </Grid>
        </Grid>
    )
}

export default ButtonsGrid;
