import { Menu } from "@mui/icons-material";
import { ClickAwayListener, Drawer, IconButton } from "@mui/material";
import { lightenHexColor } from "../../libs/utilFunctions";
import { useTheme } from "@mui/system";
import { useState } from "react";

function TopBarRightMenu(){

    const theme = useTheme();

    const onClickMenu = () => {
        setOpen((prevOpen) => !prevOpen);
    }

    const handleClickAway = () => {
        setOpen(false);
    }

    const [open, setOpen] = useState(false);

    return(
        <>
        <IconButton
        onClick={onClickMenu}
        >
            <Menu style={{color: lightenHexColor(theme.palette.primary.light, 40)}} />
        </IconButton>
        <ClickAwayListener onClickAway={handleClickAway} >
            <Drawer 
            anchor="right"
            open={open}
            >
                <h1>Ciao</h1>
            </Drawer>
        </ClickAwayListener>
        </>
    )
}

export default TopBarRightMenu;
