import { Language } from "@mui/icons-material";
import { IconButton, ListItemButton, ListItemIcon, ListItemText, Menu, MenuItem, Typography, useMediaQuery } from "@mui/material";
import { useTheme } from "@mui/system";
import { lightenHexColor } from "../../libs/utilFunctions"
import { useEffect, useState } from "react";
import "/node_modules/flag-icons/css/flag-icons.min.css";

function LanguageButton({variant}) {

    const theme = useTheme();
    const isIpadDown = useMediaQuery(theme.breakpoints.down("ipad"));
    const [selectedLanguage, setSelectedLanguage] = useState({key: "en", text: "English"});

    const languages = [
        {key: "en", text: "English", icon: (<span className="fi fi-gb" style={{borderRadius: "10px"}}></span>)},
        {key: "it", text: "Italiano", icon: (<span className="fi fi-it" style={{borderRadius: "10px"}}></span>)},
        {key: "es", text: "EspaÃ±ol", icon: (<span className="fi fi-es" style={{borderRadius: "10px"}}></span>)}
    ]

    //Desktop mode
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    }
    const handleClose = () => {
        setAnchorEl(null);
    }

    const setLanguage = (language) => {
        setSelectedLanguage(language);
        handleClose();
    }

    //Mobile mode
    const onClickMenu = () => {
        setOpen(true);
    }

    const [openDrawer, setOpenDrawer] = useState(false);
    const toggleDrawer = (open) => (event) => {
        if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }
        setOpenDrawer(open);
    };


    return(
        <>
        { variant === "desktop" ? (
            <div>
                <IconButton
                onClick={handleClick}
                >
                    <Language style={{color: lightenHexColor(theme.palette.primary.light, 40)}}/>
                    <Typography
                    align="left"
                    style={{
                        position: "absolute", 
                        zIndex: 10, 
                        bottom: "0px", 
                        right: "0px", 
                        color: lightenHexColor(theme.palette.primary.light, 40), 
                        fontSize: "9px",
                        textTransform: "uppercase"
                        }}>
                        {selectedLanguage.key}
                    </Typography>
                </IconButton>
                <Menu
                anchorEl={anchorEl}
                open={open}
                onClose={handleClose}
                anchorOrigin={{vertical: "bottom", horizontal: "center"}}>
                    {languages.map((language) => (
                        <MenuItem 
                        key={language.key}
                        onClick={() => setLanguage(language)}
                        >
                            {language.icon}
                            <div style={{marginLeft: "5px"}}/>
                            <Typography
                            variant="button"
                            style={{color: theme.palette.primary.dark}}>
                                {language.text}
                            </Typography>
                        </MenuItem>
                    ))}
                </Menu>
            </div>
            )
            : 
            (
            <div>
                <ListItemButton>
                    <ListItemIcon style={{color: lightenHexColor(theme.palette.primary.dark, 30)}}>
                        <Language />
                    </ListItemIcon>
                    <ListItemText 
                    style={{
                        color: lightenHexColor(theme.palette.primary.dark, 0),
                    }}>
                        {selectedLanguage.text}
                    </ListItemText>
                </ListItemButton>
                <Drawer 
                anchor="right"
                open={openDrawer}
                onClose={toggleDrawer(false)}
                >
                    <div style={{width: isIpadDown ? "100vw" : "40vw"}}>
                        <List>
                            {listItems.map((listItem) => (
                                <ListItemButton key={listItem.key}>
                                    <ListItemIcon style={{color: lightenHexColor(theme.palette.primary.dark, 30)}}>
                                        {listItem.icon}
                                    </ListItemIcon>
                                    <ListItemText 
                                    style={{
                                        color: lightenHexColor(theme.palette.primary.dark, 0),
                                    }}>
                                        {listItem.text}
                                    </ListItemText>
                                </ListItemButton>
                            ))
                            }
                        </List>
                        <Divider />
                        <LanguageButton variant={"mobile"} />
                        <ThemeButton variant={"mobile"} />
                    </div>
                </Drawer>
            </div>
        )}
        </>
    )
}

export default LanguageButton;
