import { ArrowBack, Explore, Login, Menu, Mood, Person, QuestionMark } from "@mui/icons-material";
import { ClickAwayListener, Divider, Drawer, IconButton, List, ListItemButton, ListItemIcon, ListItemText, useMediaQuery } from "@mui/material";
import { lightenHexColor } from "../../libs/utilFunctions";
import { useTheme } from "@mui/system";
import { useState } from "react";
import "./styles.css";
import LanguageButton from "./LanguageButton";
import ThemeButton from "./ThemeButton";
import { useNavigate } from "react-router-dom";

function TopBarRightMenu({setThemeMode}){

    const theme = useTheme();
    const isIpadDown = useMediaQuery(theme.breakpoints.down('ipad'))

    const navigate = useNavigate();

    const onClickMenu = () => {
        setOpen(true);
    }

    const [open, setOpen] = useState(false);
    const toggleDrawer = (open) => (event) => {
        if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }
        setOpen(open);
    };

    const listItems = [
        {key: "1", text: "", icon: (<ArrowBack />), path: ""},
        {key: "2", text: "Sign Up", icon: (<Person />), path: "/signup"},
        {key: "3", text: "Log In", icon: (<Login />), path: "/"},
        {key: "4", text: "Discover", icon: (<Explore />), path: "/discover"},
        {key: "5", text: "About Us", icon: (<QuestionMark />), path: "/aboutus"},
    ]

    return(
        <>
        <IconButton
        onClick={onClickMenu}
        >
            <Menu style={{color: lightenHexColor(theme.palette.primary.light, 40)}} />
        </IconButton>
        <Drawer 
        anchor="right"
        open={open}
        onClose={toggleDrawer(false)}
        >
            <div style={{width: isIpadDown ? "100vw" : "40vw"}}>
                <List>
                    {listItems.map((listItem) => (
                        <ListItemButton key={listItem.key} onClick={() => {setOpen(false); navigate(listItem.path)}}>
                            <ListItemIcon style={{color: lightenHexColor(theme.palette.primary.dark, 30)}}>
                                {listItem.icon}
                            </ListItemIcon>
                            <ListItemText 
                            style={{
                                color: lightenHexColor(theme.palette.primary.dark, 0),
                            }}>
                                {listItem.text}
                            </ListItemText>
                        </ListItemButton>
                    ))
                    }
                </List>
                <Divider />
                <LanguageButton variant={"mobile"} />
                <ThemeButton variant={"mobile"} setThemeMode={setThemeMode}/>
            </div>
        </Drawer>
        </>
    )
}

export default TopBarRightMenu;
