import { Button, Collapse, Typography } from "@mui/material";
import { useEffect, useState } from "react";
import { useSSR } from "react-i18next";

function DescriptionBox({text}) {

    const [hide, setHide] = useState(false);

    const onClickHideShow = () => {
        if(hide){
            setHide(false);
        }else{
            setHide(true);
        }
    }

    const visibleText = 100;

    const hiddenText = text.slice(0, 5);
    const nextText = text.slice(6);

    useEffect(() => {
        console.log(hide)
    })

    return(
        <div>
            <Typography>
                    <pre>{hiddenText}...</pre>
            </Typography>
            <Collapse in={hide} orientation="vertical">
                <Typography>
                    <pre>{nextText}</pre>
                </Typography>
            </Collapse>
            <Button onClick={onClickHideShow}>
                Apri
            </Button>
        </div>
    )
}

export default DescriptionBox;
