import { ThemeProvider } from "@mui/system";
import { CssBaseline, useMediaQuery } from "@mui/material";
import TopBar from "./components/TopBar";
import "./App.css";
import Authentication from "./pages/Authentication";
import { Route, Routes } from "react-router-dom";
import LoginForm from "./components/authComponents/LoginForm";
import SignupForm from "./components/authComponents/SignupForm";
import ConfirmForm from "./components/authComponents/ConfirmForm";
import ForgotPasswordCodeForm from "./components/authComponents/ForgotPasswordCodeForm";
import ForgotPasswordForm from "./components/authComponents/ForgotPasswordForm";
import BottomBar from "./components/BottomBar";



import {lightTheme, darkTheme} from "./libs/theme";
import { useState } from "react";

const [themeMode, setThemeMode] = useState(lightTheme)
const standardThemeMode = useMediaQuery('(prefers-color-scheme: dark)')
const localStorageThemeMode = window.localStorage.getItem("theme")
useEffect(() => {
    if(localStorageThemeMode){
        if(localStorageThemeMode === 'light'){
            setThemeMode(lightTheme)
        }else{
            setThemeMode(darkTheme)
        }
    }else{
        if(standardThemeMode){
            setThemeMode(darkTheme)
        }else{
            setThemeMode(lightTheme)
        }
    }
},[standardThemeMode, localStorageThemeMode])

function App() {

  return (
    <ThemeProvider theme={lightTheme}>
    <CssBaseline />

      <div className="topbar-container">
        <TopBar />
      </div>

      <Routes>
        <Route path="/" element={<Authentication />}>
          <Route path="/" element={<LoginForm/>} />
          <Route path="/signup" element={<SignupForm/>} />
          <Route path="/confirmAccount" element={<ConfirmForm/>} />
          <Route path="/forgotPasswordCode" element={<ForgotPasswordCodeForm />} />
          <Route path="/forgotPassword" element={<ForgotPasswordForm />} />
        </Route>
      </Routes>

      <div className="bottombar-container">
        <BottomBar />
      </div>

      </ThemeProvider>
  );
}

export default App;
