import { Button, Dialog, DialogTitle, Divider, Grid, IconButton, Typography, useTheme } from "@mui/material";

function SubscribePopup ({username, nickname, type, open, setOpen}){

    const theme = useTheme();

    var subsList = [
        {
            price: 10,
            days: 30
        },
        {
            price: 18,
            days: 60
        },
        {
            price: 25,
            days: 90
        }
    ]

    return(
    <div>
        <Dialog
        open={open}
        onClose={() => setOpen(false)}>
            <div>
                <Grid container padding="15px" rowSpacing={1}>
                    <Grid item xs={12}>
                        <Typography 
                        variant="h5"
                        fontWeight="bold" 
                        align="center"
                        color={theme.palette.primary.dark}
                        style={{marginTop: "10px"}}>
                            Abbonati a {nickname}
                        </Typography>
                    </Grid>
                    <Grid item xs={12}>
                        <Divider />
                    </Grid>
                    <Grid style={{marginInline: "10px"}} item xs={12}>
                        <Typography>
                            Scegli uno tra i seguenti abbonamenti, una volta selezionato
                            potrai procedere per il pagamento.
                        </Typography>
                    </Grid>
                    {subsList.map((item) => (
                        <Grid item xs={12} style={{marginInline: "10px"}}>
                            <Button 
                            fullWidth 
                            variant="contained"
                            style={{fontWeight: "bold"}}>
                                Abbonati {item.days} giorni a {item.price}
                            </Button>
                        </Grid>
                    ))}
                </Grid>
            </div>
        </Dialog>
    </div>
    )
}

export default SubscribePopup;
