import { ArrowDropDown, DarkMode, Language, LightMode } from "@mui/icons-material";
import { Button, IconButton, Menu, MenuItem, Typography } from "@mui/material";
import { useTheme } from "@mui/system";
import { lightenHexColor } from "../../libs/utilFunctions"
import { useState } from "react";
import "/node_modules/flag-icons/css/flag-icons.min.css";
import { loadingButtonClasses } from "@mui/lab";
import { lightTheme, darkTheme } from "../../libs/theme";

function ThemeButton({setThemeMode, variant}) {

    const theme = useTheme();
    const themeMode = localStorage.getItem("themeMode");

    const onClickCambia = () => {
        themeMode = localStorage.getItem("themeMode");
        if(themeMode){
            if(themeMode === "light"){
                setThemeMode(darkTheme);
                themeMode = "dark";
                window.localStorage.setItem("themeMode", "dark");
            }else{
                setThemeMode(lightTheme);
                themeMode = "light";
                window.localStorage.setItem("themeMode", "light");
            }
        }else{
            setThemeMode(darkTheme);
            themeMode = "dark"
            window.localStorage.setItem("themeMode", "dark");
        }
    }

    return(
            <>
            { variant === "desktop" ? (
                <div>
                    <IconButton
                    variant="contained"
                    onClick={onClickCambia}
                    >
                        {themeMode === "dark" ?
                            (<LightMode style={{color: lightenHexColor(theme.palette.primary.light, 40)}}/>)
                            :
                            (<DarkMode style={{color: lightenHexColor(theme.palette.primary.light, 40)}}/>)
                        }
                    </IconButton>
                    <Menu
                    anchorEl={anchorEl}
                    open={open}
                    onClose={handleClose}
                    anchorOrigin={{vertical: "bottom", horizontal: "center"}}>
                        {languages.map((language) => (
                            <MenuItem 
                            key={language.key}
                            onClick={() => setLanguage(language.key)}
                            >
                                {language.icon}
                                <div style={{marginLeft: "5px"}}/>
                                <Typography
                                variant="button"
                                style={{color: theme.palette.primary.dark}}>
                                    {language.text}
                                </Typography>
                            </MenuItem>
                        ))}
                    </Menu>
                </div>
                )
                : 
                (<div></div>)
            }
        </>
    )
}

export default ThemeButton;
