import { Menu, Mood } from "@mui/icons-material";
import { ClickAwayListener, Drawer, IconButton, List, ListItemButton, ListItemIcon, ListItemText, useMediaQuery } from "@mui/material";
import { lightenHexColor } from "../../libs/utilFunctions";
import { useTheme } from "@mui/system";
import { useState } from "react";
import "./styles.css";

function TopBarRightMenu(){

    const theme = useTheme();
    const isIpadDown = useMediaQuery(theme.breakpoints.down('ipad'))

    const onClickMenu = () => {
        setOpen(true);
    }

    const [open, setOpen] = useState(false);
    const toggleDrawer = (open) => (event) => {
        if (event && event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
          return;
        }
        setOpen(open);
    };

    const list = (
        <List>
            <ListItemButton>
                <ListItemIcon>
                    <Mood />
                </ListItemIcon>
                <ListItemText>
                    Ciaoooooo
                </ListItemText>
            </ListItemButton>
        </List>
    )
    

    return(
        <>
        <IconButton
        onClick={onClickMenu}
        >
            <Menu style={{color: lightenHexColor(theme.palette.primary.light, 40)}} />
        </IconButton>
        <Drawer 
        anchor="right"
        open={open}
        onClose={toggleDrawer(false)}
        >
            <div style={{width: isIpadDown ? "100vw" : "40vw"}}>
                {list}
            </div>
        </Drawer>
        </>
    )
}

export default TopBarRightMenu;
