import { Alert, Button, Checkbox, Divider, FormControl, FormControlLabel, FormGroup, Grid, IconButton, InputAdornment, List, ListItem, ListItemText, TextField, Typography } from "@mui/material";
import "./styles.css";
import { LoadingButton } from "@mui/lab";
import { useEffect, useState } from "react";

function SignupForm(){

    const [nickname, setNickname] = useState('');
    const [showNicknameAlert, setShowNicknameAlert] = useState(false);
    const [nicknameEmpty, setNicknameEmpty] = useState(true);

    //Funzione per controllare il campo "Nome utente"
    const onChangeNickname = (event) => {
        const inputNickname = event.target.value;
        const regex = /^[a-zA-Z0-9_.]+$/;
        setNickname(inputNickname);
        var valid = false;
        var empty = true;
        if(inputNickname === ""){
            valid = false;
            empty = true;
        }else{
            valid = regex.test(inputNickname) && inputNickname.length >= 5 && inputNickname.length <= 30;
            empty = false;
        }
        if(empty){
            setShowNicknameAlert(false);
            setNicknameEmpty(true);
        }else{
            if(valid){
                setShowNicknameAlert(false);
                setNicknameEmpty(false);
            }else{
                setShowNicknameAlert(true);
                setNicknameEmpty(false);
            }
        }
    };

    const [password, setPassword] = useState('');
    const [showPasswordAlert, setShowPasswordAlert] = useState(false);
    const [passwordAlertMessage, setPasswordAlertMessage] = useState([]);
    const [passwordEmpty, setPasswordEmpty] = useState(true);

    const onChangePassword = (event) => {
        const inputPassword = event.target.value;
        setPassword(inputPassword);

        const atLeastOneLowercaseLetter = /(?=.*[a-z])/; // Almeno una lettera minuscola
        const atLeastOneUppercaseLetter = /(?=.*[A-Z])/; // Almeno una lettera maiuscola
        const atLeastOneDigit = /(?=.*\d)/; // Almeno un numero
        const atLeastOneSpecialChar = /(?=.*[$*{}()?!@#.%&><':;|_=+\-,])/; // Almeno un carattere speciale tra $*{}()?!@#.%&><':;|_=+-,
        const validLength = /^.{8,20}$/; // Lunghezza minima di 8 caratteri e massima di 20 caratteri
        var valid = false;
        var empty = true;
        if(inputPassword === ""){
            valid = false;
            empty = true;
        }else{
            empty = false;
            setPasswordAlertMessage([]);
            if(!atLeastOneLowercaseLetter.test(inputPassword)){
                const lowerCaseMessage = (
                    <Typography fontSize="13px">
                        <li>Deve contenere almeno una lettera minuscola.</li> 
                    </Typography>
                )
                setPasswordAlertMessage((passwordAlertMessage) => [...passwordAlertMessage, lowerCaseMessage])
                valid = false;
            }
        }
        if(empty){
            setShowPasswordAlert(false);
            setPasswordEmpty(true);
        }else{
            if(valid){
                setShowPasswordAlert(false);
                setPasswordEmpty(false);
            }else{
                setShowPasswordAlert(true);
                setPasswordEmpty(false);
            }
        }
    }

    return(
        <div className="signup-container">
            <FormControl component="form">
                <Grid container rowSpacing={2} columnSpacing={1} display="flex">
                    <Grid item xs={12}>
                        <Typography variant="h4" align="center" color="primary" fontWeight="bold">
                            Registrati
                        </Typography>
                    </Grid>
                    <Grid item xs={12}>
                        <Alert
                        severity="error" 
                        >
                            <Typography 
                            variant="body2">
                                Messaggio di errore
                            </Typography>
                        </Alert>
                    </Grid>
                    <Grid item xs={12}>
                        <TextField 
                        autoComplete="username"
                        type="text"
                        label="Nome utente"
                        fullWidth
                        onChange={onChangeNickname}
                        variant="outlined" />
                        
                        <Alert 
                        severity="error"
                        style={{marginTop: "3px", display: !showNicknameAlert && "none"}}
                        >
                            <Typography fontSize="15px">Errore sul nome utente:</Typography>
                            <ul style={{marginLeft: "-24px", marginTop: "0px", marginBottom: "0px"}}>
                            <Typography fontSize="13px">
                                <li>Deve contenere dai 5 ai 30 caratteri.</li> 
                            </Typography>
                            <Typography fontSize="13px">
                                <li>Può contenere solo lettere, numeri e i caratteri "_" e "."</li>
                            </Typography>
                            </ul>
                        </Alert>
                        
                    </Grid>
                    <Grid item xs={12}>
                        <TextField 
                        autoComplete="email"
                        type="email"  
                        label="Email" 
                        variant="outlined" 
                        fullWidth
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        autoComplete="new-password"
                        type="password"
                        label="Password"
                        variant="outlined"
                        fullWidth
                        onChange={onChangePassword}
                        />
                        <Alert
                        severity="error"
                        style={{marginTop: "3px", display: !showPasswordAlert && "none"}}
                        >
                            <Typography fontSize="15px">Errore sul formato della password:</Typography>
                            <ul style={{marginLeft: "-24px", marginTop: "0px", marginBottom: "0px"}}>
                            <Typography fontSize="13px">
                                <li>Errore 1</li> 
                            </Typography>
                            <Typography fontSize="13px">
                                <li>Ecc...</li>
                            </Typography>
                            </ul>
                        </Alert>
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        autoComplete="new-password"
                        type="password"
                        label="Ripeti la password"
                        variant="outlined"
                        fullWidth
                        />
                        {/*Alert sul ripeti password
                        <Alert
                        severity="error"
                        style={{marginTop: "3px"}}
                        >
                            <Typography fontSize="15px">Le password non coincidono.</Typography>
                        </Alert>
                    */}
                    </Grid>
                    <Grid item xs={12}>
                        <FormControlLabel 
                        style={{marginBottom: "-10px"}}
                        control={
                        <Checkbox 
                        style={{transform: "scale(0.8)"}}
                        />
                        }
                        label={
                            <Typography 
                            marginLeft="-5px"
                            fontSize="15px">
                                Accetta i nostri termini e condizioni.
                            </Typography>
                        }/>
                        <FormControlLabel 
                        control={
                        <Checkbox 
                        style={{transform: "scale(0.8)"}}
                        />
                        } 
                        label={
                            <Typography 
                            marginLeft="-5px"
                            fontSize="15px">
                                Ho più di 18 anni.
                            </Typography>
                        }/>
                    </Grid>
                    <Grid item xs={12}>
                        <Grid container >
                            <Grid item xs={12}>
                                <LoadingButton 
                                type="submit"
                                id="signup_button" 
                                variant="contained"
                                fullWidth
                                >
                                    Effettua registrazione
                                </LoadingButton>
                            </Grid>
                            <Grid item xs={12}>
                                <Typography 
                                align="right"
                                variant="subtitle1" 
                                fontSize="15px"
                                style={{cursor: "pointer", marginTop:"9px", marginRight: "5px", marginBottom: "10px"}}>
                                    Hai già un account? Effettua l'accesso.
                                </Typography>
                            </Grid>
                        </Grid>
                        

                    </Grid>
                </Grid>
            </FormControl>
        </div>
    )
}

export default SignupForm;
