import { Button, Dialog, DialogTitle, Divider, Grid, IconButton, Typography, useTheme } from "@mui/material";

function SubscribePopup ({username, nickname, type, open, setOpen}){

    const theme = useTheme();

    return(
    <div>
        <Dialog
        open={open}
        onClose={() => setOpen(false)}
        >
            <div className="subscribe-popup-container">
                <Grid container rowSpacing={1}>
                    <Grid item xs={12}>
                        <Typography variant="h5" fontWeight="bold" color={theme.palette.primary.dark}>
                            Abbonati a {nickname}
                        </Typography>
                    </Grid>
                    <Divider/>
                    <Grid item xs={12}>
                        <Button className="subscribe-popup-button">Abbonati 30 giorni a 10€</Button>
                    </Grid>
                    <Grid item>
                        <Button>Abbonati 60 giorni a 17€</Button>
                    </Grid>
                    <Grid item>
                        <Button>Abbonati 100 giorni a 2€</Button>
                    </Grid>
                </Grid>
            </div>
        </Dialog>
    </div>
    )
}

export default SubscribePopup;
