            switch (error.message) {
                case "Username cannot be empty":
                    setLoading(false)
                    setAlert(true)
                    setMessage("Il campo della mail non può essere vuoto.")
                    break;
                case "User is already confirmed.":
                    setAlert(true)
                    setLoading(false)
                    setMessage("L'utente è già stato confermato.")
                    setTimeout(function(){window.location.href="/"}, 6000)
                    break;
                case "Username/client id combination not found.":
                    setAlert(true)
                    setMessage("L'email non è associata a nessun account.")
                    setLoading(false)
                    break;
                case "Attempt limit exceeded, please try after some time.":
                    setAlert(true)
                    setMessage("Hai effettuato troppi tentativi. Riprova più tardi.")
                    setLoading(false)
                    break;
                default:
                    setAlert(true)
                    setMessage("")
                    setLoading(false)
                    break;
            }
        })*/
    }

    //Variabili per il settaggio del caricamento del loading button, del testo del messaggio dell'alert,
    //dell'attivazione dell'alert, del check del "ricordami".
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState([]);
    const [alert, setAlert] = useState(false);
    const [confirmAlert, setConfirmAlert] = useState(false);

    //Funzione che si esegue quando clicchi sul pulsante "Esegui l'accesso".
    //AAA: Quando aggiungeremo il backend bisogna configurare attentamente questa funzione.
    const onPressConfirm = async(e) =>{
        e.preventDefault()
        setLoading(true);
        setAlert(false);
        setMessage([]);

        const mainAlertMessages = () => {
            setMessage([])
            const messages = [
                { key: "emptyFormError", message: "Verifica che i campi non siano vuoti.", visible: passwordEmpty || repeatPasswordEmpty || codeEmpty},
                { key: "codeInvalid", message: "Verifica che il codice inserito sia in un formato valido.", visible: !codeValid && !codeEmpty},
                { key: "invalidError", message: "Verifica che tutti i campi siano stati compilati correttamente.", visible: showPasswordAlert || showRepeatPasswordAlert }
            ]
            return messages;
        };

        const messages = mainAlertMessages();
        const allConditionsSatisfied = messages.every((message) => !message.visible);

        if(!allConditionsSatisfied){
            setAlert(true);
            setMessage(messages);
            setLoading(false);
        }else{
            setAlert(false);
            setMessage([]);
            /*
            await Auth.confirmSignUp(email, code)
            .then(() =>{
                
                setConfirmAlert(true);
                setTimeout(() => {
                    localStorage.removeItem("email");
                    window.location.href="/"
                }, 2500)
                
            })
            .catch((error) => {
                switch (error.message) {
                    case "Confirmation code cannot be empty":
                        setAlert(true)
                        setLoading(false)
                        setMessage("Il campo del codice non può essere vuoto.")
                        break;
                    case "Username cannot be empty":
                        setLoading(false)
                        setAlert(true)
                        setMessage("Il campo della mail non può essere vuoto.")
                        break;
                    case "Invalid verification code provided, please try again.":
                        setLoading(false)
                        setAlert(true)
                        setMessage("Il codice inserito non è valido, per favore ripeti.")
                        break;
                    case "User cannot be confirmed. Current status is CONFIRMED":
                        setLoading(false)
                        setAlert(true)
                        setMessage("L'utente è già stato confermato.")
                        setTimeout(function(){window.location.href="/"}, 6000)
                        break;
                    case "Username/client id combination not found.":
                        setLoading(false)
                        setAlert(true)
                        setMessage("L'email non è associata a nessun account.")
                        break;
                    case "Attempt limit exceeded, please try after some time.":
                        setLoading(false)
                        setAlert(true)
                        setMessage("Hai tentato troppe volte, per favore ripeti più avanti.")
                        break;
                    default:
                        switch (error.name) {
                            case "Il formato del codice non è valido, verifica che non ci siano spazi.":
                                setLoading(false)
                                setAlert(true)
                                setMessage()
                                break;
                            default:
                                setLoading(false)
                                setAlert(true)
                                setMessage("")
                                break;
                        }
                        break;
                }
            })*/
        }
    }

    return(
        <div className="confirm-container">
            <FormControl component="form">
                <Grid container rowSpacing={2} columnSpacing={1} display="flex">
                    <Grid item xs={12}>
                        <Typography variant="h4" align="center" color="primary" fontWeight="bold">
                            Recupera Password
                        </Typography>
                    </Grid>
                    <Grid item xs={12}>
                        <div 
                        id="text_container" 
                        style={{padding: "10px", backgroundColor: theme.palette.background.paper, borderRadius: "10px"}}>
                            <Typography 
                            variant="body1"
                            align="left">
                                Inserisci una nuova password e il codice ottenuto all'indirizzo:{" "}
                                <Typography fontWeight="bold" component="span">{email}</Typography>
                            </Typography>
                        </div>
                    </Grid>
                    <Grid item xs={12}>
                        <Alert
                        severity="error"
                        style={{marginTop: "3px", display: !alert && "none"}}
                        >
                            <ul style={{marginLeft: "-24px", marginTop: "0px", marginBottom: "0px"}}>
                            {message.map((message) => (
                                message.visible && (
                                <Typography key={message.key} fontSize="13px">
                                <li>{message.message}</li>
                                </Typography>
                                )
                            ))}
                            </ul>
                        </Alert>
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        autoComplete="new-password"
                        type="password"
                        label="Password"
                        variant="outlined"
                        fullWidth
                        onChange={onChangePassword}
                        />
                        <Alert
                        severity="error"
                        style={{marginTop: "3px", display: !showPasswordAlert && "none"}}
                        >
                            <Typography fontSize="15px">Errore sul formato della password:</Typography>
                            <ul style={{marginLeft: "-24px", marginTop: "0px", marginBottom: "0px"}}>
                            {passwordAlertMessage.map((message) => (
                                message.visible && (
                                <Typography key={message.key} fontSize="13px">
                                <li>{message.message}</li>
                                </Typography>
                                )
                            ))}
                            </ul>
                        </Alert>
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        autoComplete="new-password"
                        type="password"
                        label="Ripeti la password"
                        variant="outlined"
                        value={repeatPassword}
                        fullWidth
                        onChange={onChangeRepeatPassword}
                        />
                        <Alert
                        severity="error"
                        style={{marginTop: "3px", display: !showRepeatPasswordAlert && "none"}}
                        onChange={onChangeRepeatPassword}
                        >
                            <Typography fontSize="15px">Le password non coincidono.</Typography>
                        </Alert>
                    </Grid>
                    <Grid item xs={12}>
                        <TextField
                        type="text"
                        label="Codice"
                        variant="outlined"
                        value={code === ""? "" : code}
                        fullWidth
                        onChange={onChangeCode}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <Grid item xs={12}>
                            <LoadingButton
                            type="submit"
                            variant="contained" 
                            fullWidth
                            loading={loading}
                            onClick={onPressConfirm}
                            >
                                Conferma
                            </LoadingButton>
                        </Grid>
                    </Grid>
                </Grid>
            </FormControl>
            <Snackbar
            open={confirmAlert} 
            autoHideDuration={2500} 
            anchorOrigin={{vertical: "bottom", horizontal: "right"}} 
            >
                <Alert severity="success">
                    Account confermato con successo!
                </Alert>
            </Snackbar>
            <Snackbar
            open={resendCodeAlert} 
            autoHideDuration={4000} 
            anchorOrigin={{vertical: "bottom", horizontal: "right"}} 
            >
                <Alert severity="success">
                    Il codice è stato inviato all'indirizzo:{" "}
                    <Typography fontWeight="bold" fontSize="14px" component="span">{email}</Typography>
                </Alert>
            </Snackbar>
        </div>
    )
}

