import { ArrowDropDown, Language } from "@mui/icons-material";
import { Button, IconButton, Menu, MenuItem } from "@mui/material";
import { useTheme } from "@mui/system";
import { lightenHexColor } from "../../libs/utilFunctions"
import { useState } from "react";
import "/node_modules/flag-icons/css/flag-icons.min.css";

function LanguageButton() {

    const theme = useTheme();

    const languages = [
        {key: "en", text: "English", icon: (<span className="fi fi-gb"></span>)},
        {key: "it", text: "Italiano", icon: (<span className="it it-it"></span>)},
        {key: "es", text: "Español", icon: (<span className="es"></span>)}
    ]

    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    }
    const handleClose = () => {
        setAnchorEl(null);
    }

    const setLanguage = (language) => {
        console.log(language);
        handleClose();
    }

    return(
        <>
            <IconButton
            variant="contained"
            onClick={handleClick}
            >
                <Language style={{color: lightenHexColor(theme.palette.primary.light, 40)}}/>
            </IconButton>
            <Menu
            anchorEl={anchorEl}
            open={open}
            onClose={handleClose}>
                {languages.map((language) => (
                    <MenuItem 
                    key={language.key}
                    onClick={() => setLanguage(language.key)}
                    >
                        {language.icon}
                        {language.text}
                    </MenuItem>
                ))}
            </Menu>
        </>
    )
}

export default LanguageButton;
